.btn {
  border: none;
  color: #aaaaaa;
  border-radius: 5px;
  padding: 7px 32px;
  cursor: pointer;
  text-decoration: none;
}
.btn:focus {
  outline: 2px solid #762e88;
}
.btn--primary {
  background-color: #370943;
}
.btn--primary:hover {
  background-color: #762e88;
  outline: 0;
}
.btn--secondary {
  background-color: #1d191d;
}
.btn--secondary:hover {
  background-color: #762e88;
}
.btn--tertiary {
  background-color: #131212;
  width: 80px;
  height: 42px;
  padding: 2px;
  transition: background-color 0.1s ease-in;
}
.btn--tertiary:focus {
  background-color: #1d191d;
  outline: 0;
}
.btn--tertiary:hover {
  background-color: #1d191d;
  outline: 0;
}
.btn--tertiary::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 0;
  background-color: #762e88;
  border-radius: 5px;
  z-index: -1;
  transition: transform 0.2s ease-in;
}
.btn--tertiary--active::before {
  transform: translateX(-6px);
  opacity: 1;
}
.btn--nav {
  background-color: transparent;
  padding: 0;
  margin: 0;
  height: 75px;
  width: 75px;
}
.btn--nav svg > ellipse:first-child {
  transition: 0.2s transform ease-in;
}
.btn--nav--active svg > ellipse:first-child {
  transform: translateX(-9px);
}
.btn--nav:focus {
  background-color: #1d191d;
  outline: 0;
}
.btn--nav:hover {
  background-color: #1d191d;
  background-color: pink;
  outline: 0;
}
.btn--nav:hover svg {
  background-color: #1d191d;
}
.btn--nav svg {
  pointer-events: none;
}
.btn--form-icon {
  border-radius: 0px;
  padding: 1px;
  height: 35px;
  width: 45px;
  background-color: #131212;
  border-bottom: 2px solid #aaaaaa;
  display: flex;
  justify-content: center;
  align-items: center;
}
.btn--form-icon svg {
  pointer-events: none;
}
.btn--icon {
  background-color: transparent;
  height: 45px;
  width: 35px;
  padding: 0;
}
.btn--pagination {
  height: 28px;
  width: 28px;
  padding: 2px;
  background-color: #1d191d;
  position: relative;
}
.btn--pagination::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 0;
  background-color: #762e88;
  border-radius: 5px;
  z-index: -1;
}
.btn--pagination--active::before {
  left: -4px;
  opacity: 1;
}

