.svg {
  fill: none;

  &--nav {
    cursor: pointer;
    height: 75px;
    width: 75px;

    &:nth-of-type(1) {
      background-color: $green-dark;
    }

    &:nth-of-type(2),
    &:nth-of-type(4) {
      background-color: $red-dark;
    }

    &:nth-of-type(3) {
      background-color: $purple-dark;
    }
  }

  &--plus,
  &--minus {
    height: 13px;
    width: 13px;
  }

  &--swap {
    height: 22px;
    width: 12px;
  }

  &--dropdown {
    height: 13px;
    width: 15px;
  }

  &--tick {
    height: 16px;
    width: 21px;
  }

  &--chevron {
    height: 16px;
    width: 26px;
  }

  &--plus-circle {
    height: 30px;
    width: 30px;
    pointer-events: none;
  }

  &--loading {
    fill: none;
    width: 100px;
    height: 100px;

    & path {
      fill: $grey-light;
    }
