sources.scss\\components\\pie-streak.scss:
.c-pie-streak {
  background-color: $grey-dark;
  // background-color: green;
  grid-column: 4 / -1;
  grid-row: 1 / span 6;
  border-radius: 10px;
  padding: 20px;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;

  @media only screen and (max-width: $bp-largest) {
    padding: 12px;
  }

  @media only screen and (max-width: $bp-large) {
    grid-column: 4 / -1;
    grid-row: 7 / -1;
  }

  & h3,
  p,
  span {
    @media only screen and (max-width: $bp-large) {
      margin-top: 10px;
    }
  }

  &__total-wrapper {
    display: flex;
    align-items: center;
    justify-content: space-between;

    @media only screen and (max-width: $bp-large) {
      flex-direction: column;
    }
  }

  &__heading,
  &__win-heading,
  &__loss-heading,
  &__streak-profit-heading,
  &__streak-loss-heading {
    @include ts-heading-2;
    text-align: left;
  }

  &__heading {
    @media only screen and (max-width: $bp-large) {
      margin-top: 8px !important;
    }
  }

  &__total,
  &__streak-profit-total,
  &__streak-loss-total {
    @include ts-numerical;
    @include ts-emphasis;
    border-radius: 10px;
    border: 2px solid $purple-light;
    display: inline-block;
    padding: 6px 20px;
    color: $purple-light;
  }

  &__streak-profit-total {
    color: $green-light;
    border: 2px solid $green-light;
  }

  &__streak-loss-total {
    color: $red-light;
    border: 2px solid $red-light;
  }

  &__visualisation-wrapper {
    height: 150px;
    display: flex;
    justify-content: flex-start;

    @media only screen and (max-width: $bp-large) {
      flex-direction: column;
      height: 250px;
    }
  }

  &__canvas {
    height: 100%;
    width: 50%;
    background-color: $grey-dark;
    justify-self: flex-start;

    @media only screen and (max-width: $bp-large) {
      width: 100%;
      margin: 0 auto;
    }
  }

  &__legend-wrapper {
    width: 50%;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    text-align: left;
    justify-self: flex-end;

    @media only screen and (max-width: $bp-large) {
      flex-direction: row;
      width: 100%;
    }
  }

  &__legend-long-wrapper,
  &__legend-short-wrapper {
    display: flex;
    flex-direction: column;
  }

  &__legend-long-label,
  &__legend-long-numbers,
  &__legend-short-label,
  &__legend-short-numbers {
    @include ts-numerical;
  }

  &__legend-long-numbers,
  &__legend-short-numbers {
    margin-top: 10px;
  }

  &__win-wrapper,
  &__loss-wrapper {
    margin-top: 14px;
    display: flex;
    justify-content: space-between;

    @media only screen and (max-width: $bp-large) {
      flex-direction: column;
      align-items: center;
    }
  }

  &__win-inner-wrapper,
  &__loss-inner-wrapper {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: space-around;
  }

  &__win-date,
  &__loss-date {
    @include ts-date;
  }

  &__win-streak-total,
  &__loss-streak-total {
    @include ts-numerical;
    @include ts-emphasis;
    color: $green-light;
    border: 2px solid $green-light;
    border-radius: 50%;
    display: inline-block;
    padding: 10px 14px;
  }

  &__loss-streak-total {
    color: $red-light;
    border: 2px solid $red-light;
  }

  &__streak-profit-wrapper,
  &__streak-loss-wrapper {
    margin-top: 14px;
    display: flex;
    justify-content: space-between;
    align-items: center;

    @media only screen and (max-width: $bp-large) {
      flex-direction: column;
    }
  }
}

sources.scss\\components\\table-profitable.scss:
.c-table-profitable {
  background-color: $grey-dark;
  // background-color: purple;
  grid-column: 1 / span 2;
  grid-row: 7 / -1;
  border-radius: 10px;
  padding: 0 2px 10px 2px;
  display: flex;
  flex-direction: column;
  position: relative;

  @media only screen and (max-width: $bp-large) {
    grid-column: 1 / span 3;
    grid-row: 7 / span 6;
  }

  &__heading {
    @include ts-heading-2;
    margin-top: 20px;
    margin: 20px 0 10px;
  }

  &__table-wrapper {
    flex-grow: 1;
  }

  &__table {
    color: $grey-light;
    width: 100%;
    text-align: center;
    // height: 100%;

    & th,
    td {
      padding: 14px 5px;
    }

    & tr:nth-of-type(1) {
      border-bottom: 1px solid $grey-light;
    }

    & th ~ th {
      border-right: 1px solid $brown-light;
    }

    & th:first-child {
      padding-left: 0;
      min-width: 70px;
    }

    & th + td {
      color: $green-light;
    }

    & td:nth-of-type(3) {
      color: $green-light;
    }

    td {
      border-right: 1px solid $brown-light;
    }

    & tr:first-of-type {
      line-height: 22px;
    }

    & td:last-of-type,
    th:last-of-type {
      border-right: none;
    }

    & tr > th:first-of-type {
      border-right: 1px solid $grey-light;
    }
  }
}
