    & tr:nth-of-type(1) {
      border-bottom: 1px solid $grey-light;
    }

    & th {
      border-right: 1px solid $grey-light;
      line-height: 22px;
    }

    & th ~ th {
      border-right: 1px solid $brown-light;
    }

    & th:first-child {
      padding-left: 0;
      min-width: 76px;
    }

    & th + td {
      color: $green-light;
    }

    td {
      border-right: 1px solid $brown-light;
    }

    & td:last-of-type,
    th:last-of-type {
      border-right: 1px solid $grey-light;
    }

    & td:nth-of-type(3) {
      color: $green-light;
    }
  }

  &__table-unit {
    border-bottom: 1px solid $grey-light;
  }
}

